geodude.prototype.drawFront = function() {
    push();
	translate(this.position.x, this.position.y);
    scale(this.size * 0.35);
    strokeWeight(1);	
	stroke(0);
	
   	push();
	fill(189, 180, 136);

	//right arm
	push();
	translate(70, -80);
	push();
    translate(-95, 55);
    rotate(Math.PI / 40);
    rect(380, -20, 80, 60, 20);
    rect(382, -18, 18, 30, 20);
    rect(401, -20, 20, 30, 20);
    rect(422, -20, 18, 30, 20);
    rect(441, -18, 15, 30, 20);
    rect(380, 10, 40, 20, 20);
    pop();
	push();
	drawPart(this.rightArm, 1);
	pop();
	pop();
	
	//leftArm
	push();
	translate(-70, -70);
	push();
	translate(-345, 115);
    rotate(Math.PI / -40);
    rect(380, -20, 80, 60, 20);
    rect(382, -18, 18, 30, 20);
    rect(401, -20, 20, 30, 20);
    rect(422, -20, 18, 30, 20);
    rect(441, -18, 15, 30, 20);
    rect(380, 10, 40, 20, 20);
	pop();
	push();
	drawPart(this.leftArm, 1);
	pop();
    pop();
	
	
	
	fill(189, 180, 136);
    rect(140, 140, 120, 120, 100);
    fill(255, 255, 255);
    ellipse(170, 192, 30, 40);
    ellipse(230, 192, 30, 40);
    fill(0);
    ellipse(225, 192, 10, 15);
    ellipse(175, 192, 10, 15);
    noFill();
    bezier(145, 230, 150, 220,250, 220, 255, 230);
    fill(189, 180, 136);
    quad(195, 185, 165, 165, 155, 170, 185, 195); 
    quad(205, 185, 235, 165, 245, 170, 215, 195);
	
    pop();
};

geodude.prototype.drawBack = function() {
    push();
	translate(this.position.x, this.position.y);
    scale(this.size * 0.35);
    strokeWeight(1);	
	stroke(0);
	
   	push();
	fill(189, 180, 136);

	//right arm
	push();
	translate(70, -80);
	push();
    translate(-95, 55);
    rotate(Math.PI / 40);
    rect(380, -20, 80, 60, 20);
    pop();
	push();
	drawPart(this.rightArm, 1);
	pop();
	pop();
	
	//leftArm
	push();
	translate(-70, -70);
	push();
	translate(-345, 115);
    rotate(Math.PI / -40);
    rect(380, -20, 80, 60, 20);
	pop();
	push();
	drawPart(this.leftArm, 1);
	pop();
    pop();
	
	
	
	fill(189, 180, 136);
    rect(140, 140, 120, 120, 100);

    
	
    pop();
};