snorlax.prototype.drawFront = function() {
    push();
	translate(this.position.x-58, this.position.y-38);
	translate((1-this.size)*50, 0);
    scale(this.size * 0.3);
    strokeWeight(1);	
	stroke(0);
	
   	push();
	fill(23, 93, 207);
	translate(20, 10);
	drawPart(this.leftArm, 1);
	pop();
	push();
	fill(23, 93, 207);
	translate(-15, 20);
	drawPart(this.rightArm, 1);
	pop();
	
	//feet
	push();
	translate(80, -30);
	rotate(Math.PI / 16);
	fill(247, 228, 161);
    ellipse(130, 360, 100, 50);
    fill(255, 255, 255);
    triangle(95, 370, 115, 375, 105, 395);
    triangle(120, 375, 140, 375, 130, 395);
    triangle(145, 375, 165, 370, 155, 395);
	pop();
	push();
	translate(-70, 47);
	rotate(Math.PI / -16);
	fill(247, 228, 161);
	ellipse(270, 360, 100, 50);
	fill(255, 255, 255);
    triangle(305, 370, 285, 375, 295, 395);
    triangle(280, 375, 260, 375, 270, 395);
    triangle(255, 375, 235, 370, 245, 395);
	pop();
	//claws
	fill(255, 255, 255);
	push();
	translate(20, 10);
	triangle(20, 285, 30, 290, 20, 310);
    triangle(31, 290, 41, 290, 33, 315);
    triangle(42, 290, 52, 285, 48, 310);
	pop();
	push();
	translate(-15, 20);
    triangle(380, 285, 370, 290, 380, 310);
    triangle(369, 290, 359, 290, 367, 315);
    triangle(358, 290, 348, 285, 352, 310);
	pop();
	
	push();
	fill(23, 93, 207);
	drawPart(this.body, 1);
	pop();
	fill(23, 93, 207);
	push();
	translate(0, 15);
    triangle(120, 40, 180, 50, 110, -30);
    triangle(270, 40, 210, 50, 280, -30);
    rect(110, 0, 170, 100, 50);
	noStroke();
    fill(247, 228, 161);
    triangle(165, 20, 225, 55, 135, 45);
    triangle(235, 20, 175, 55, 255, 45);
    rect(132, 38, 127, 50, 20);
    triangle(145, 80, 247, 80, 200, 150);
	pop();
    push();
	translate(0, -40);
    fill(247, 228, 161);
	noStroke();
	drawPart(this.belly, 1);
	pop();
    stroke(0);
	push();
	translate(0, 15);
	line(155, 40, 180, 40);
    line(210, 40, 235, 40);
    line(165, 70, 225, 70);
    fill(255, 255, 255);
    triangle(165, 70, 175, 70, 170, 60);
    triangle(225, 70, 215, 70, 220, 60);
	pop();
	stroke(0);
	//line(150, 115, 240, 115);
	noFill();
	//bezier(150, 115, 160, 100, 230, 100, 240, 115);
	bezier(150, 110, 160, 120, 230, 120, 240, 110);
    pop();
};

