//Trainer Class

var trainerObj = function(x, y, char, initialLook)
{
    this.position = new createVector(x, y);
    this.step = new createVector(0, 0);
    this.wanderAngle = 0;
    this.wanderDist = random(70, 100);    
    this.tailEnd = new createVector(65, 40);
    this.tailDir = new createVector(55, 40);
    this.tailMove = true;
    this.i = initialLook;
    this.currFrame = frameCount;
    this.char = char;
};


